// RegistrarAsignaturaProfesor.jsx
import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';
import asignatura from './Asignatura';

const RegistrarAsignaturaProfesor = ({ navigation }) => {
  const [nombre, setNombre] = useState('');
  const [dia, setDia] = useState('');
  const [horario, set] = useState('');
  const [ubicacion, setUbicacion] = useState('');
  const [descripcion, setDescripcion] = useState('');

  const handleEnviar = () => {
    // Guardar la asignatura en el array
    const nuevaAsignatura = {
      nombre,
      dia,
      horario,
      ubicacion,
      descripcion,
    };
    asignatura.push(nuevaAsignatura);

    // Redirigir a la landing page
    navigation.navigate('LandingPage');
  };

  return (
    <View style={styles.container}>
      <Text>Registrar Asignatura</Text>
      <TextInput
        style={styles.input}
        placeholder="Nombre"
        value={nombre}
        onChangeText={setNombre}
      />
      <TextInput
        style={styles.input}
        placeholder="Digite los dias de la asesoria seguidos ejm: Lunes- Jueves"
        value={dia}
        onChangeText={setDia}
      />
      <TextInput
        style={styles.input}
        placeholder="hora de inicio y fin ejm: 12:00-13:00"
        value={horario}
        onChangeText={set}
      />
      <TextInput
        style={styles.input}
        placeholder="Ubicación"
        value={ubicacion}
        onChangeText={setUbicacion}
      />
      <TextInput
        style={styles.input}
        placeholder="Descripción"
        value={descripcion}
        onChangeText={setDescripcion}
      />
      <Button title="Enviar" onPress={handleEnviar} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 12,
    paddingHorizontal: 10,
  },
});

export default RegistrarAsignaturaProfesor;
