// RegistrarAsignaturaEstudiante.jsx
import React, { useState } from 'react';
import { View, Text, Picker, Button, StyleSheet } from 'react-native';
import { getAsignatura } from './Asignatura';
import { useRoute } from '@react-navigation/native';
import { getUsuario } from './Usuario'; // Importa la funciÃ³n getUsuario

const RegistrarAsignaturaEstudiante = ({ navigation }) => {
  const [selectedAsignatura, setSelectedAsignatura] = useState(null);
  const route = useRoute();
  const { cedula } = route.params;

  const handleEnviar = () => {
    // Verificar si se ha seleccionado una asignatura
    if (selectedAsignatura) {
      // Usa getUsuario para obtener los datos del estudiante
      const estudiante = getUsuario(cedula);
      const nombreAsignatura = selectedAsignatura.nombre || 'Nombre no disponible';
      alert(`Estudiante: ${estudiante.nombre} ${estudiante.apellido}\nHas seleccionado la asignatura: ${nombreAsignatura}`);
    } else {
      alert('Por favor, selecciona una asignatura.');
    }
  };

  const navigateToAsignaturaEstudiante = () => {
    navigation.navigate('AsignaturaEstudiante', { cedula });
  };

  return (
    <View style={styles.container}>
      <Text>Registrar Asignatura</Text>

      {/* Lista desplegable (Picker) para seleccionar la asignatura */}
      <Picker
        selectedValue={selectedAsignatura}
        style={styles.picker}
        onValueChange={(itemValue, itemIndex) => setSelectedAsignatura(itemValue)}>
        <Picker.Item label="Selecciona una asignatura..." value={null} />
        {getAsignatura().map((asignaturaItem, index) => (
          <Picker.Item
            key={index}
            label={asignaturaItem.nombre || 'Nombre no disponible'}
            value={asignaturaItem}
          />
        ))}
      </Picker>

      <Button title="Enviar" onPress={handleEnviar} />
      <Button title="Ver asignaturas registradas" onPress={navigateToAsignaturaEstudiante} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  picker: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 12,
  },
});

export default RegistrarAsignaturaEstudiante;
