import React, { useState } from 'react';
import { View, Text, Image, TextInput, Button, StyleSheet } from 'react-native';
import axios from 'axios'; 

const Login = ({ navigation }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = () => {
        // Agrega aquí la lógica de autenticación, similar a lo que tenías en Main.jsx
        // ...
        axios.post('http://localhost:19006/login', { email, password })
        .then(response => {
            if (response.data.success && response.data.user.rol === 'estudiante') {
                navigation.navigate('Student', { userData: response.data.user });
            } else {
                console.log('Credenciales inválidas o no eres un estudiante');
            }
        })
        .catch(error => {
            console.error('Error en la autenticación:', error);
        });

        if (user && user.password === password) {
            navigation.navigate('Main', { studentName: user.email });
        } else {
            console.log('Credenciales inválidas');
        }
    };

    return (
        <View style={styles.container}>
            {/* Espacio del encabezado (puedes ajustar el estilo según tus necesidades) */}
            <View style={styles.header}>
                <Image
                    source={require('../components/img/sai.png')}
                    style={styles.logo}
                    resizeMode="contain"
                />
                <Text style={styles.institutionText}>Institución Universitaria de Envigado</Text>
                <Text style={styles.welcomeText}>Bienvenido a Asesoría Ágil</Text>
            </View>

            {/* Input de ingresar usuario */}
            <TextInput
                style={styles.input}
                placeholder="Correo electrónico"
                value={email}
                onChangeText={(text) => setEmail(text)}
            />

            {/* Input de ingresar contraseña */}
            <TextInput
                style={styles.input}
                placeholder="Contraseña"
                secureTextEntry={true}
                value={password}
                onChangeText={(text) => setPassword(text)}
            />

            {/* Botón de ingresar */}
            <Button title="Ingresar" onPress={handleLogin} />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 16,
    },
    header: {
        alignItems: 'center',
        marginBottom: 20,
    },
    logo: {
        width: 100,
        height: 100,
    },
    institutionText: {
        fontSize: 14,
        fontWeight: 'bold',
        marginTop: 10,
    },
    welcomeText: {
        fontSize: 27,
        marginBottom: 10,
        color: '#52738C',

    },
    input: {
        height: 40,
        borderColor: 'gray',
        borderWidth: 1,
        marginBottom: 12,
        paddingHorizontal: 10,
    },
});

export default Login;
