import React from 'react';
import { View, Text, Image, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import usuario from './Usuario';

const Administrador = ({ route }) => {
    const { nombre, foto, cedula } = route.params;
    const navigation = useNavigation();
    const administrador = usuario.find(user => user.cedula === cedula);

    return (
        <View style={styles.container}>
            {/* Barra de navegación */}
            <View style={styles.navBar}>
                <TouchableOpacity onPress={() => navigation.goBack()}>
                    <Text style={styles.navText}>Atrás</Text>
                </TouchableOpacity>
            </View>

            {/* Encabezado */}
            <View style={styles.header}>
                <Image source={administrador.foto} style={styles.profileImage} />
                <View style={styles.profileInfo}>
                    <Text>{administrador.rol}</Text>
                    <Text>{`${administrador.nombre} ${administrador.apellido}`}</Text>
                </View>
            </View>

            {/* Cuerpo de la página */}
            <ScrollView style={styles.body}>
                <Text style={styles.greetingText}>Buenas tardes, {nombre}</Text>
                <Text style={styles.welcomeText}>Bienvenido a Asesoria Agil</Text>
                <Text style={styles.paragraph}>
                    Descubre nuestro Servicio de Administración en la Universidad de Envigado.
                    Con opciones personalizadas y herramientas administrativas, facilitamos
                    la gestión de asesorías. Administra usuarios, asignaturas y más. Una experiencia
                    que optimiza el proceso de asesorías en tu institución.
                </Text>

                {/* Botones */}
                <View style={styles.buttonContainer}>
                    <TouchableOpacity style={styles.button}>
                        <Text style={styles.buttonText}>Administrar Usuarios</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.button}>
                        <Text style={styles.buttonText}>Administrar Asignaturas</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.button}>
                        <Text style={styles.buttonText}>Generar Reportes</Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>

            {/* Pie de página */}
            <View style={styles.footer}>
                <TouchableOpacity onPress={() => navigation.navigate('Login')}>
                    <Text style={styles.footerText}>Regresar al Login</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    // Estilos (mismos estilos que en Estudiante.jsx)
});

export default Administrador;
