import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, StyleSheet, Picker } from 'react-native';
import { setAsignatura, getAsignatura } from './Asignatura';
import { useNavigation, useRoute } from '@react-navigation/native';

const RegistrarAsignaturaEstudiante = ({ navigation }) => {
  const [nombre, setNombre] = useState('');
  const [dia, setDia] = useState('');
  const [horario, setHorario] = useState('');
  const [ubicacion, setUbicacion] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [selectedAsignatura, setSelectedAsignatura] = useState('');
  const [asignaturasRegistradas, setAsignaturasRegistradas] = useState([]);

  const route = useRoute();
  const { cedula } = route.params;

  useEffect(() => {
    // Obtener las asignaturas registradas al cargar el componente
    setAsignaturasRegistradas(getAsignatura());
  }, []);

  const handleEnviar = () => {
    // Guardar la asignatura en el array
    const nuevaAsignatura = {
      nombre,
      dia,
      horario,
      ubicacion,
      descripcion,
    };
    setAsignatura([...getAsignatura(), nuevaAsignatura]);  // Actualizar el array de asignaturas

    // Reiniciar el formulario
    setNombre('');
    setDia('');
    setHorario('');
    setUbicacion('');
    setDescripcion('');
  };

  return (
    <View style={styles.container}>
      <Text>Registrar Asignatura</Text>

      {/* Lista desplegable (Picker) para seleccionar la asignatura */}
      <Picker
        selectedValue={selectedAsignatura}
        style={styles.input}
        onValueChange={(itemValue, itemIndex) => setSelectedAsignatura(itemValue)}>
        <Picker.Item label="Selecciona una asignatura..." value={null} />
        {asignaturasRegistradas.map((asignaturaItem, index) => (
          <Picker.Item
            key={index}
            label={`${asignaturaItem.nombre} - ${asignaturaItem.dia} - ${asignaturaItem.horario}`}
            value={asignaturaItem.nombre}
          />
        ))}
      </Picker>

      <TextInput
        style={styles.input}
        placeholder="Nombre"
        value={nombre}
        onChangeText={setNombre}
      />
      <TextInput
        style={styles.input}
        placeholder="Digite los días de la asesoría seguidos ejm: Lunes- Jueves"
        value={dia}
        onChangeText={setDia}
      />
      <TextInput
        style={styles.input}
        placeholder="Hora de inicio y fin ejm: 12:00-13:00"
        value={horario}
        onChangeText={setHorario}
      />
      <TextInput
        style={styles.input}
        placeholder="Ubicación"
        value={ubicacion}
        onChangeText={setUbicacion}
      />
      <TextInput
        style={styles.input}
        placeholder="Descripción"
        value={descripcion}
        onChangeText={setDescripcion}
      />
      <Button title="Enviar" onPress={handleEnviar} />
      <Button title="Ver asignaturas registradas" />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    width: '80%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 12,
    paddingHorizontal: 10,
  },
});

export default RegistrarAsignaturaEstudiante;
