// AsignaturaEstudiante.jsx
import React from 'react';
import { View, Text, StyleSheet, Button, Image } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getAsignatura } from './Asignatura';

const AsignaturaEstudiante = ({ route }) => {
  const { cedula, selectedAsignatura } = route.params;
  const navigation = useNavigation();

  return (
    <View style={styles.container}>
      {/* Barra de navegaci칩n */}
      <View style={styles.navBar}>
        <Text style={styles.navText} onPress={() => navigation.goBack()}>
          Atr치s
        </Text>
      </View>

      {/* Cuerpo de la p치gina */}
      <View style={styles.body}>
        <Text>Asignatura Seleccionada</Text>
        {selectedAsignatura ? (
          <View style={styles.containerAsignatura}>
            <Image style={styles.image} source={require('./img/metodologias.png')} />
            <Text style={styles.title}>{selectedAsignatura.nombre}</Text>
            <Text style={styles.subtitle}>{selectedAsignatura.descripcion}</Text>
          </View>
        ) : (
          <Text>No has seleccionado ninguna asignatura.</Text>
        )}
      </View>

      {/* Pie de p치gina */}
      <View style={styles.footer}>
        <Text style={styles.footerText} onPress={() => navigation.navigate('Login')}>
          Regresar al Login
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
  },
  navBar: {
    backgroundColor: '#D88A0D',
    padding: 10,
  },
  navText: {
    color: '#FFFFFF',
    fontSize: 16,
  },
  body: {
    padding: 16,
    alignItems: 'center',
  },
  containerAsignatura: {
    alignItems: 'center',
    backgroundColor: '#F2F2F2',
    padding: 16,
    borderRadius: 8,
    marginBottom: 16,
  },
  image: {
    width: 150,
    height: 150,
    marginRight: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  subtitle: {
    fontSize: 15,
  },
  footer: {
    padding: 10,
    borderTopWidth: 1,
    borderTopColor: '#CCCCCC',
    alignItems: 'center',
  },
  footerText: {
    color: '#007BFF',
  },
});

export default AsignaturaEstudiante;
